      AND dispatch_info_id = #{dispatchInfoId, jdbcType=BIGINT}


      AND dispatch_apply_id = #{dispatchApplyId, jdbcType=BIGINT}


      AND dispatch_number = #{dispatchNumber, jdbcType=VARCHAR}


      AND dispatch_effective_time = #{dispatchEffectiveTime, jdbcType=TIMESTAMP}


      AND dispatch_status = #{dispatchStatus, jdbcType=VARCHAR}


      AND carrier_name = #{carrierName, jdbcType=VARCHAR}


      AND driver_name = #{driverName, jdbcType=VARCHAR}


      AND driver_phone = #{driverPhone, jdbcType=VARCHAR}


      AND car_license = #{carLicense, jdbcType=VARCHAR}


