mapper.delete:
DELETE FROM t_psi_order_follow_record
WHERE

       AND order_follow_id = #{orderFollowId, jdbcType=BIGINT}

DELETE FROM t_psi_order_follow_record
WHERE


      AND order_follow_id = #{orderFollowId, jdbcType=BIGINT}


      AND order_info_id = #{orderInfoId, jdbcType=BIGINT}


      AND follow_status = #{followStatus, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_psi_order_follow_record a


      AND a.order_follow_id = #{orderFollowId, jdbcType=BIGINT}


      AND a.order_info_id = #{orderInfoId, jdbcType=BIGINT}


      AND a.follow_status = #{followStatus, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.is_enable = #{isEnable, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}

SELECT count(1) FROM t_psi_order_follow_record a


      AND a.order_follow_id = #{orderFollowId, jdbcType=BIGINT}


      AND a.order_info_id = #{orderInfoId, jdbcType=BIGINT}


      AND a.follow_status = #{followStatus, jdbcType=VARCHAR}


