mapper.delete:
DELETE FROM t_psi_sales_payment_record
WHERE

       AND record_id = #{recordId, jdbcType=BIGINT}

DELETE FROM t_psi_sales_payment_record
WHERE


      AND record_id = #{recordId, jdbcType=BIGINT}


      AND record_code = #{recordCode, jdbcType=VARCHAR}


      AND order_info_id = #{orderInfoId, jdbcType=BIGINT}


      AND order_no = #{orderNo, jdbcType=VARCHAR}


      AND payment_item = #{paymentItem, jdbcType=VARCHAR}


      AND payment_amount = #{paymentAmount, jdbcType=DECIMAL}


      AND serial_number = #{serialNumber, jdbcType=VARCHAR}


      AND proceeds_by = #{proceedsBy, jdbcType=BIGINT}


      AND payment_method = #{paymentMethod, jdbcType=VARCHAR}


      AND pay_status = #{payStatus, jdbcType=VARCHAR}


      AND payment_account = #{paymentAccount, jdbcType=VARCHAR}


      AND account_name = #{accountName, jdbcType=VARCHAR}


      AND create_by = #{createBy, jdbcType=BIGINT}


      AND create_date = #{createDate, jdbcType=TIMESTAMP}


      AND update_by = #{updateBy, jdbcType=BIGINT}


      AND update_date = #{updateDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_psi_sales_payment_record a


      AND a.record_id = #{recordId, jdbcType=BIGINT}


      AND a.record_code = #{recordCode, jdbcType=VARCHAR}


      AND a.order_info_id = #{orderInfoId, jdbcType=BIGINT}


      AND a.order_no = #{orderNo, jdbcType=VARCHAR}


      AND a.payment_item = #{paymentItem, jdbcType=VARCHAR}


      AND a.payment_amount = #{paymentAmount, jdbcType=DECIMAL}


      AND a.serial_number = #{serialNumber, jdbcType=VARCHAR}


      AND a.proceeds_by = #{proceedsBy, jdbcType=BIGINT}


      AND a.payment_method = #{paymentMethod, jdbcType=VARCHAR}


      AND a.pay_status = #{payStatus, jdbcType=VARCHAR}


      AND a.payment_account = #{paymentAccount, jdbcType=VARCHAR}


      AND a.account_name = #{accountName, jdbcType=VARCHAR}


      AND a.create_by = #{createBy, jdbcType=BIGINT}


      AND a.create_date = #{createDate, jdbcType=TIMESTAMP}


      AND a.update_by = #{updateBy, jdbcType=BIGINT}


      AND a.update_date = #{updateDate, jdbcType=TIMESTAMP}

    order by a.create_date desc

