mapper.delete:
DELETE FROM t_psi_transfer_route_info
WHERE

       AND route_id = #{routeId, jdbcType=BIGINT}

DELETE FROM t_psi_transfer_route_info
WHERE


      AND route_id = #{routeId, jdbcType=BIGINT}


      AND dispatch_info_id = #{dispatchInfoId, jdbcType=BIGINT}


      AND current_address = #{currentAddress, jdbcType=VARCHAR}


      AND record_time = #{recordTime, jdbcType=TIMESTAMP}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_psi_transfer_route_info a


      AND a.route_id = #{routeId, jdbcType=BIGINT}


      AND a.dispatch_info_id = #{dispatchInfoId, jdbcType=BIGINT}


      AND a.current_address = #{currentAddress, jdbcType=VARCHAR}


      AND a.record_time = #{recordTime, jdbcType=TIMESTAMP}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.is_enable = #{isEnable, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}

