mapper.delete:
DELETE FROM t_resource_identifier
WHERE

       AND identifier_id = #{identifierId, jdbcType=BIGINT}

DELETE FROM t_resource_identifier
WHERE


      AND identifier_id = #{identifierId, jdbcType=BIGINT}


      AND permission_id = #{permissionId, jdbcType=BIGINT}


      AND identifier_name = #{identifierName, jdbcType=VARCHAR}


      AND identifier_path = #{identifierPath, jdbcType=VARCHAR}


      AND identifier_type = #{identifierType, jdbcType=VARCHAR}


      AND access_type = #{accessType, jdbcType=VARCHAR}


      AND is_valid = #{isValid, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_resource_identifier a


      AND a.identifier_id = #{identifierId, jdbcType=BIGINT}


      AND a.permission_id = #{permissionId, jdbcType=BIGINT}


      AND a.identifier_name = #{identifierName, jdbcType=VARCHAR}


      AND a.identifier_path = #{identifierPath, jdbcType=VARCHAR}


      AND a.identifier_type = #{identifierType, jdbcType=VARCHAR}


      AND a.access_type = #{accessType, jdbcType=VARCHAR}


      AND a.is_valid = #{isValid, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_resource_identifier a


