mapper.delete:
DELETE FROM t_resource_info
WHERE

       AND resource_id = #{resourceId, jdbcType=BIGINT}

DELETE FROM t_resource_info
WHERE


      AND resource_id = #{resourceId, jdbcType=BIGINT}


      AND application_id = #{applicationId, jdbcType=BIGINT}


      AND parent_resource_id = #{parentResourceId, jdbcType=BIGINT}


      AND resource_name = #{resourceName, jdbcType=VARCHAR}


      AND resource_content = #{resourceContent, jdbcType=VARCHAR}


      AND resource_desc = #{resourceDesc, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_resource_info a


      AND a.resource_id = #{resourceId, jdbcType=BIGINT}


      AND a.application_id = #{applicationId, jdbcType=BIGINT}


      AND a.parent_resource_id = #{parentResourceId, jdbcType=BIGINT}


      AND a.resource_name = #{resourceName, jdbcType=VARCHAR}


      AND a.resource_content = #{resourceContent, jdbcType=VARCHAR}


      AND a.resource_desc = #{resourceDesc, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_resource_info a


