mapper.delete:
DELETE FROM t_sys_car_used_storage
WHERE

       AND storage_id = #{storageId, jdbcType=BIGINT}

DELETE FROM t_sys_car_used_storage
WHERE


      AND storage_id = #{storageId, jdbcType=BIGINT}


      AND storage_name = #{storageName, jdbcType=VARCHAR}


      AND storage_address = #{storageAddress, jdbcType=VARCHAR}


      AND storage_code = #{storageCode, jdbcType=VARCHAR}


      AND manager_name = #{managerName, jdbcType=VARCHAR}


      AND manager_phone = #{managerPhone, jdbcType=VARCHAR}


      AND city = #{city, jdbcType=VARCHAR}


      AND city_id = #{cityId, jdbcType=VARCHAR}


      AND province = #{province, jdbcType=VARCHAR}


      AND province_id = #{provinceId, jdbcType=VARCHAR}


      AND district = #{district, jdbcType=VARCHAR}


      AND district_id = #{districtId, jdbcType=VARCHAR}


      AND inventory_capacity = #{inventoryCapacity, jdbcType=INTEGER}


      AND default_move_address = #{defaultMoveAddress, jdbcType=VARCHAR}


      AND org_id = #{orgId, jdbcType=BIGINT}


      AND remarks = #{remarks, jdbcType=VARCHAR}


      AND is_manual_creation = #{isManualCreation, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

