mapper.delete:
DELETE FROM t_sys_datadict
WHERE

       AND dict_id = #{dictId, jdbcType=BIGINT}

DELETE FROM t_sys_datadict
WHERE


      AND dict_id = #{dictId, jdbcType=BIGINT}


      AND dict_tag = #{dictTag, jdbcType=VARCHAR}


      AND dict_code = #{dictCode, jdbcType=VARCHAR}


      AND dict_prefix = #{dictPrefix, jdbcType=VARCHAR}


      AND dict_value = #{dictValue, jdbcType=VARCHAR}


      AND dict_status = #{dictStatus, jdbcType=CHAR}


      AND dict_remarks = #{dictRemarks, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND t_sys_datadictcol = #{tSysDatadictcol, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_sys_datadict a


      AND a.dict_id = #{dictId, jdbcType=BIGINT}


      AND a.dict_tag = #{dictTag, jdbcType=VARCHAR}


      AND a.dict_code = #{dictCode, jdbcType=VARCHAR}


      AND a.dict_prefix = #{dictPrefix, jdbcType=VARCHAR}


      AND a.dict_value = #{dictValue, jdbcType=VARCHAR}


      AND a.dict_status = #{dictStatus, jdbcType=CHAR}


      AND a.dict_remarks = #{dictRemarks, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.t_sys_datadictcol = #{tSysDatadictcol, jdbcType=VARCHAR}

SELECT count(1) FROM t_sys_datadict a


      AND a.dict_id = #{dictId, jdbcType=BIGINT}


      AND a.dict_tag = #{dictTag, jdbcType=VARCHAR}


      AND a.dict_code = #{dictCode, jdbcType=VARCHAR}


      AND a.dict_prefix = #{dictPrefix, jdbcType=VARCHAR}


      AND a.dict_value = #{dictValue, jdbcType=VARCHAR}


      AND a.dict_status = #{dictStatus, jdbcType=CHAR}


      AND a.dict_remarks = #{dictRemarks, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.t_sys_datadictcol = #{tSysDatadictcol, jdbcType=VARCHAR}
