mapper.delete:
DELETE FROM t_sys_department_info
WHERE

       AND department_id = #{departmentId, jdbcType=BIGINT}

DELETE FROM t_sys_department_info
WHERE


      AND department_id = #{departmentId, jdbcType=BIGINT}


      AND department_code = #{departmentCode, jdbcType=VARCHAR}


      AND department_name = #{departmentName, jdbcType=VARCHAR}


      AND department_type = #{departmentType, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_sys_department_info a


      AND a.department_id = #{departmentId, jdbcType=BIGINT}


      AND a.department_code = #{departmentCode, jdbcType=VARCHAR}


      AND a.department_name = #{departmentName, jdbcType=VARCHAR}


      AND a.department_type = #{departmentType, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_sys_department_info a


      AND a.department_id = #{departmentId, jdbcType=BIGINT}


