mapper.delete:
DELETE FROM t_sys_dept_info
WHERE

       AND dept_info_id = #{deptInfoId, jdbcType=BIGINT}

DELETE FROM t_sys_dept_info
WHERE


      AND dept_info_id = #{deptInfoId, jdbcType=BIGINT}


      AND hhr_dept_code = #{hhrDeptCode, jdbcType=VARCHAR}


      AND hhr_status = #{hhrStatus, jdbcType=VARCHAR}


      AND hhr_dept_zh_name = #{hhrDeptZhName, jdbcType=VARCHAR}


      AND hhr_dept_zh_short_name = #{hhrDeptZhShortName, jdbcType=VARCHAR}


      AND hhr_dept_en_name = #{hhrDeptEnName, jdbcType=VARCHAR}


      AND hhr_dept_en_short_name = #{hhrDeptEnShortName, jdbcType=VARCHAR}


      AND hhr_dept_level = #{hhrDeptLevel, jdbcType=VARCHAR}


      AND hhr_dept_higher_dept = #{hhrDeptHigherDept, jdbcType=VARCHAR}


      AND hhr_org_dept_attr02 = #{hhrOrgDeptAttr02, jdbcType=VARCHAR}


      AND hhr_part_dept = #{hhrPartDept, jdbcType=VARCHAR}


      AND hhr_org_dept_attr09 = #{hhrOrgDeptAttr09, jdbcType=VARCHAR}


      AND hhr_org_dept_attr06 = #{hhrOrgDeptAttr06, jdbcType=VARCHAR}


      AND hhr_org_dept_attr05 = #{hhrOrgDeptAttr05, jdbcType=VARCHAR}


      AND hhr_org_dept_attr08 = #{hhrOrgDeptAttr08, jdbcType=VARCHAR}


      AND hhr_org_dept_attr07 = #{hhrOrgDeptAttr07, jdbcType=VARCHAR}


      AND hhr_org_dept_attr10 = #{hhrOrgDeptAttr10, jdbcType=VARCHAR}


      AND hhr_dept_detail_desc = #{hhrDeptDetailDesc, jdbcType=VARCHAR}


      AND last_update_date = #{lastUpdateDate, jdbcType=TIMESTAMP}


      AND version = #{version, jdbcType=INTEGER}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

