mapper.delete:
DELETE FROM t_sys_file_msg
WHERE

       AND file_id = #{fileId, jdbcType=BIGINT}

DELETE FROM t_sys_file_msg
WHERE


      AND file_id = #{fileId, jdbcType=BIGINT}


      AND file_type = #{fileType, jdbcType=VARCHAR}


      AND file_name = #{fileName, jdbcType=VARCHAR}


      AND file_path = #{filePath, jdbcType=VARCHAR}


      AND file_statuss = #{fileStatuss, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_sys_file_msg a


      AND a.file_id = #{fileId, jdbcType=BIGINT}


      AND a.file_type = #{fileType, jdbcType=VARCHAR}


      AND a.file_name = #{fileName, jdbcType=VARCHAR}


      AND a.file_path = #{filePath, jdbcType=VARCHAR}


      AND a.file_statuss = #{fileStatuss, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_sys_file_msg a


      AND a.file_id = #{fileId, jdbcType=BIGINT}


      AND a.file_type = #{fileType, jdbcType=VARCHAR}


      AND a.file_name = #{fileName, jdbcType=VARCHAR}


      AND a.file_path = #{filePath, jdbcType=VARCHAR}


      AND a.file_statuss = #{fileStatuss, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}
