mapper.update:
UPDATE t_sys_key_word SET 
      updated_by = #{updatedBy, jdbcType=BIGINT},
      updated_date = #{updatedDate, jdbcType=TIMESTAMP},
      is_delete = #{isDelete, jdbcType=VARCHAR}
 WHERE
  key_words = #{keyWords, jdbcType=VARCHAR}
  and key_words_type = #{keyWordsType, jdbcType=INTEGER}

mapper.delete:
DELETE FROM t_sys_key_word
WHERE

       AND key_words_id = #{keyWordsId, jdbcType=BIGINT}

DELETE FROM t_sys_key_word
WHERE


      AND key_words_id = #{keyWordsId, jdbcType=BIGINT}


      AND key_words = #{keyWords, jdbcType=VARCHAR}


      AND key_words_type = #{keyWordsType, jdbcType=INTEGER}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_sys_key_word a


      AND a.key_words_id = #{keyWordsId, jdbcType=BIGINT}


      AND a.key_words = #{keyWords, jdbcType=VARCHAR}


      AND a.key_words_type = #{keyWordsType, jdbcType=INTEGER}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}


  order by a.created_date desc

SELECT count(1) FROM t_sys_key_word a


      AND a.key_words_id = #{keyWordsId, jdbcType=BIGINT}


      AND a.key_words = #{keyWords, jdbcType=VARCHAR}


      AND a.key_words_type = #{keyWordsType, jdbcType=INTEGER}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}
