mapper.delete:
DELETE FROM t_sys_material_file
WHERE

       AND material_file_id = #{materialFileId, jdbcType=BIGINT}

DELETE FROM t_sys_material_file
WHERE


      AND material_file_id = #{materialFileId, jdbcType=BIGINT}


      AND file_id = #{fileId, jdbcType=BIGINT}


      AND upload_person = #{uploadPerson, jdbcType=BIGINT}


      AND upload_date = #{uploadDate, jdbcType=TIMESTAMP}


      AND material_order = #{materialOrder, jdbcType=INTEGER}


      AND thumbnail = #{thumbnail, jdbcType=LONGVARCHAR}

mapper.select:
SELECT 

  FROM t_sys_material_file a


      AND a.material_file_id = #{materialFileId, jdbcType=BIGINT}


      AND a.file_id = #{fileId, jdbcType=BIGINT}


      AND a.upload_person = #{uploadPerson, jdbcType=BIGINT}


      AND a.upload_date = #{uploadDate, jdbcType=TIMESTAMP}


      AND a.material_order = #{materialOrder, jdbcType=INTEGER}


      AND a.thumbnail = #{thumbnail, jdbcType=LONGVARCHAR}

SELECT count(1) FROM t_sys_material_file a


      AND a.material_file_id = #{materialFileId, jdbcType=BIGINT}


      AND a.file_id = #{fileId, jdbcType=BIGINT}


      AND a.upload_person = #{uploadPerson, jdbcType=BIGINT}


      AND a.upload_date = #{uploadDate, jdbcType=TIMESTAMP}


      AND a.material_order = #{materialOrder, jdbcType=INTEGER}


      AND a.thumbnail = #{thumbnail, jdbcType=LONGVARCHAR}
