mapper.delete:
DELETE FROM t_sys_param_config
WHERE

       AND param_id = #{paramId, jdbcType=BIGINT}

DELETE FROM t_sys_param_config
WHERE


      AND param_id = #{paramId, jdbcType=BIGINT}


      AND param_type = #{paramType, jdbcType=VARCHAR}


      AND param_profile = #{paramProfile, jdbcType=VARCHAR}


      AND param_details = #{paramDetails, jdbcType=VARCHAR}


      AND param_values = #{paramValues, jdbcType=VARCHAR}


      AND param_unit = #{paramUnit, jdbcType=VARCHAR}


      AND param_unit_details = #{paramUnitDetails, jdbcType=VARCHAR}


      AND param_sort = #{paramSort, jdbcType=TINYINT}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_sys_param_config a


      AND a.param_id = #{paramId, jdbcType=BIGINT}


      AND a.param_type = #{paramType, jdbcType=VARCHAR}


      AND a.param_profile = #{paramProfile, jdbcType=VARCHAR}


      AND a.param_details = #{paramDetails, jdbcType=VARCHAR}


      AND a.param_values = #{paramValues, jdbcType=VARCHAR}


      AND a.param_unit = #{paramUnit, jdbcType=VARCHAR}


      AND a.param_unit_details = #{paramUnitDetails, jdbcType=VARCHAR}


      AND a.param_sort = #{paramSort, jdbcType=TINYINT}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.is_enable = #{isEnable, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}

SELECT count(1) FROM t_sys_param_config a


