mapper.delete:
DELETE FROM t_sys_partner_city_rela
WHERE

       AND city_id = #{cityId, jdbcType=BIGINT}

DELETE FROM t_sys_partner_city_rela
WHERE


      AND city_id = #{cityId, jdbcType=BIGINT}


      AND partner_id = #{partnerId, jdbcType=BIGINT}


      AND region_code = #{regionCode, jdbcType=BIGINT}


      AND city_area = #{cityArea, jdbcType=VARCHAR}


      AND city_province = #{cityProvince, jdbcType=VARCHAR}


      AND city_name = #{cityName, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
