      AND a.city_area = #{cityArea, jdbcType=VARCHAR}


      AND a.city_province = #{cityProvince, jdbcType=VARCHAR}


      AND a.city_name = #{cityName, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


