mapper.delete:
DELETE FROM t_sys_pay_config
WHERE

       AND revert_body = #{revertBody, jdbcType=VARCHAR}

DELETE FROM t_sys_pay_config
WHERE


      AND revert_body = #{revertBody, jdbcType=VARCHAR}


      AND app_id = #{appId, jdbcType=VARCHAR}


      AND secret = #{secret, jdbcType=VARCHAR}


      AND app_code = #{appCode, jdbcType=VARCHAR}


      AND merchant_number = #{merchantNumber, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_sys_pay_config a


      AND a.revert_body = #{revertBody, jdbcType=VARCHAR}


      AND a.app_id = #{appId, jdbcType=VARCHAR}


      AND a.secret = #{secret, jdbcType=VARCHAR}


      AND a.app_code = #{appCode, jdbcType=VARCHAR}


      AND a.merchant_number = #{merchantNumber, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.is_enable = #{isEnable, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}

SELECT count(1) FROM t_sys_pay_config a


      AND a.revert_body = #{revertBody, jdbcType=VARCHAR}


      AND a.app_id = #{appId, jdbcType=VARCHAR}


      AND a.secret = #{secret, jdbcType=VARCHAR}


      AND a.app_code = #{appCode, jdbcType=VARCHAR}


      AND a.merchant_number = #{merchantNumber, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


