mapper.delete:
DELETE FROM t_sys_pricing_strategy
WHERE

       AND strategy_id = #{strategyId, jdbcType=BIGINT}

DELETE FROM t_sys_pricing_strategy
WHERE


      AND strategy_id = #{strategyId, jdbcType=BIGINT}


      AND rule_id = #{ruleId, jdbcType=BIGINT}


      AND strategy_name = #{strategyName, jdbcType=VARCHAR}


      AND strategy_type = #{strategyType, jdbcType=VARCHAR}


      AND strategy_no = #{strategyNo, jdbcType=VARCHAR}


      AND value_type = #{valueType, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_sys_pricing_strategy a


      AND a.strategy_id = #{strategyId, jdbcType=BIGINT}


      AND a.rule_id = #{ruleId, jdbcType=BIGINT}


      AND a.strategy_name = #{strategyName, jdbcType=VARCHAR}


      AND a.strategy_type = #{strategyType, jdbcType=VARCHAR}


      AND a.strategy_no = #{strategyNo, jdbcType=VARCHAR}


      AND a.value_type = #{valueType, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.is_enable = #{isEnable, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}

SELECT count(1) FROM t_sys_pricing_strategy a


      AND a.strategy_id = #{strategyId, jdbcType=BIGINT}


      AND a.rule_id = #{ruleId, jdbcType=BIGINT}


      AND a.strategy_name = #{strategyName, jdbcType=VARCHAR}


      AND a.strategy_type = #{strategyType, jdbcType=VARCHAR}


      AND a.strategy_no = #{strategyNo, jdbcType=VARCHAR}


      AND a.value_type = #{valueType, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


