mapper.delete:
DELETE FROM t_sys_pricing_strategy_details_section
WHERE

       AND strategy_details_id = #{strategyDetailsId, jdbcType=BIGINT}

DELETE FROM t_sys_pricing_strategy_details_section
WHERE


      AND strategy_details_id = #{strategyDetailsId, jdbcType=BIGINT}


      AND strategy_id = #{strategyId, jdbcType=BIGINT}


      AND details_type = #{detailsType, jdbcType=VARCHAR}


      AND left_value = #{leftValue, jdbcType=INTEGER}


      AND right_value = #{rightValue, jdbcType=INTEGER}


      AND details_value = #{detailsValue, jdbcType=VARCHAR}


      AND is_source = #{isSource, jdbcType=INTEGER}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_sys_pricing_strategy_details_section a


      AND a.strategy_details_id = #{strategyDetailsId, jdbcType=BIGINT}


      AND a.strategy_id = #{strategyId, jdbcType=BIGINT}


      AND a.details_type = #{detailsType, jdbcType=VARCHAR}


      AND a.left_value = #{leftValue, jdbcType=INTEGER}


      AND a.right_value = #{rightValue, jdbcType=INTEGER}


      AND a.details_value = #{detailsValue, jdbcType=VARCHAR}


      AND a.is_source = #{isSource, jdbcType=INTEGER}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND a.is_enable = #{isEnable, jdbcType=VARCHAR}


      AND a.is_delete = #{isDelete, jdbcType=VARCHAR}

SELECT count(1) FROM t_sys_pricing_strategy_details_section a


