mapper.delete:
DELETE FROM t_sys_right_activities_details
WHERE

       AND detail_id = #{detailId, jdbcType=BIGINT}

DELETE FROM t_sys_right_activities_details
WHERE


      AND detail_id = #{detailId, jdbcType=BIGINT}


      AND equity_id = #{equityId, jdbcType=BIGINT}


      AND right_id = #{rightId, jdbcType=BIGINT}


      AND right_name = #{rightName, jdbcType=VARCHAR}


      AND right_type_name = #{rightTypeName, jdbcType=VARCHAR}


      AND sub_type_name = #{subTypeName, jdbcType=VARCHAR}


      AND status = #{status, jdbcType=INTEGER}


      AND unit = #{unit, jdbcType=VARCHAR}


      AND meno = #{meno, jdbcType=VARCHAR}


      AND coupon = #{coupon, jdbcType=VARCHAR}


      AND pool = #{pool, jdbcType=VARCHAR}


      AND coupon_name = #{couponName, jdbcType=VARCHAR}


      AND pool_name = #{poolName, jdbcType=VARCHAR}


      AND limited = #{limited, jdbcType=INTEGER}


      AND integral = #{integral, jdbcType=BIGINT}


      AND deadline = #{deadline, jdbcType=BIGINT}


      AND countless = #{countless, jdbcType=INTEGER}


      AND target = #{target, jdbcType=INTEGER}


      AND model_code = #{modelCode, jdbcType=VARCHAR}


      AND model_name = #{modelName, jdbcType=VARCHAR}


      AND shape_code = #{shapeCode, jdbcType=VARCHAR}


      AND shape_name = #{shapeName, jdbcType=VARCHAR}


      AND timeless = #{timeless, jdbcType=INTEGER}


      AND details_trigger = #{detailsTrigger, jdbcType=INTEGER}


      AND effect_time = #{effectTime, jdbcType=TIMESTAMP}


      AND expire_time = #{expireTime, jdbcType=TIMESTAMP}


      AND is_enable = #{isEnable, jdbcType=VARCHAR}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

