mapper.delete:
DELETE FROM t_sys_role_details
WHERE

       AND role_id = #{roleId, jdbcType=BIGINT}

DELETE FROM t_sys_role_details
WHERE


      AND role_id = #{roleId, jdbcType=BIGINT}


      AND role_code = #{roleCode, jdbcType=VARCHAR}


      AND role_details_type = #{roleDetailsType, jdbcType=VARCHAR}


      AND role_details_name = #{roleDetailsName, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_sys_role_details a


      AND a.role_id = #{roleId, jdbcType=BIGINT}


      AND a.role_code = #{roleCode, jdbcType=VARCHAR}


      AND a.role_details_type = #{roleDetailsType, jdbcType=VARCHAR}


      AND a.role_details_name = #{roleDetailsName, jdbcType=VARCHAR}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_sys_role_details a


