mapper.delete:
DELETE FROM t_sys_staff_area_log
WHERE

       AND log_id = #{logId, jdbcType=BIGINT}

DELETE FROM t_sys_staff_area_log
WHERE


      AND log_id = #{logId, jdbcType=BIGINT}


      AND log_ope_person_id = #{logOpePersonId, jdbcType=BIGINT}


      AND ope_person_id = #{opePersonId, jdbcType=BIGINT}


      AND log_ope_content = #{logOpeContent, jdbcType=VARCHAR}


      AND log_ope_date = #{logOpeDate, jdbcType=TIMESTAMP}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}

mapper.select:
SELECT 

  FROM t_sys_staff_area_log a


      AND a.log_id = #{logId, jdbcType=BIGINT}


      AND a.log_ope_person_id = #{logOpePersonId, jdbcType=BIGINT}


      AND a.ope_person_id = #{opePersonId, jdbcType=BIGINT}


      AND a.log_ope_content = #{logOpeContent, jdbcType=VARCHAR}


      AND a.log_ope_date = #{logOpeDate, jdbcType=TIMESTAMP}


      AND a.created_by = #{createdBy, jdbcType=BIGINT}


      AND a.created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND a.updated_by = #{updatedBy, jdbcType=BIGINT}


      AND a.updated_date = #{updatedDate, jdbcType=TIMESTAMP}

SELECT count(1) FROM t_sys_staff_area_log a


