mapper.delete:
DELETE FROM t_sys_store_staff_details
WHERE

       AND party_id = #{partyId, jdbcType=BIGINT}

DELETE FROM t_sys_store_staff_details
WHERE


      AND party_id = #{partyId, jdbcType=BIGINT}


      AND staff_code = #{staffCode, jdbcType=VARCHAR}


      AND staff_name = #{staffName, jdbcType=VARCHAR}


      AND staff_email = #{staffEmail, jdbcType=VARCHAR}


      AND staff_sex = #{staffSex, jdbcType=VARCHAR}


      AND staff_iphone = #{staffIphone, jdbcType=VARCHAR}


      AND staff_status = #{staffStatus, jdbcType=VARCHAR}


      AND staff_type = #{staffType, jdbcType=VARCHAR}


      AND created_by = #{createdBy, jdbcType=BIGINT}


      AND created_date = #{createdDate, jdbcType=TIMESTAMP}


      AND updated_by = #{updatedBy, jdbcType=BIGINT}


      AND updated_date = #{updatedDate, jdbcType=TIMESTAMP}


      AND is_delete = #{isDelete, jdbcType=VARCHAR}

mapper.select:
