mapper.delete:
DELETE FROM t_sys_user_behavior
WHERE

       AND behavior_id = #{behaviorId, jdbcType=BIGINT}

DELETE FROM t_sys_user_behavior
WHERE


      AND behavior_id = #{behaviorId, jdbcType=BIGINT}


      AND user_id = #{userId, jdbcType=BIGINT}


      AND behavior_type = #{behaviorType, jdbcType=CHAR}


      AND behavior_target = #{behaviorTarget, jdbcType=VARCHAR}


      AND behavior_target_url = #{behaviorTargetUrl, jdbcType=VARCHAR}


      AND behavior_time = #{behaviorTime, jdbcType=BIGINT}


      AND behavior_endpoint = #{behaviorEndpoint, jdbcType=VARCHAR}

mapper.select:
SELECT 

  FROM t_sys_user_behavior a


      AND a.behavior_id = #{behaviorId, jdbcType=BIGINT}


      AND a.user_id = #{userId, jdbcType=BIGINT}


      AND a.behavior_type = #{behaviorType, jdbcType=CHAR}


      AND a.behavior_target = #{behaviorTarget, jdbcType=VARCHAR}


      AND a.behavior_target_url = #{behaviorTargetUrl, jdbcType=VARCHAR}


      AND a.behavior_time = #{behaviorTime, jdbcType=BIGINT}


      AND a.behavior_endpoint = #{behaviorEndpoint, jdbcType=VARCHAR}

SELECT count(1) FROM t_sys_user_behavior a


      AND a.behavior_id = #{behaviorId, jdbcType=BIGINT}


      AND a.user_id = #{userId, jdbcType=BIGINT}


      AND a.behavior_type = #{behaviorType, jdbcType=CHAR}


      AND a.behavior_target = #{behaviorTarget, jdbcType=VARCHAR}


      AND a.behavior_target_url = #{behaviorTargetUrl, jdbcType=VARCHAR}


      AND a.behavior_time = #{behaviorTime, jdbcType=BIGINT}


