mapper.delete:
DELETE FROM t_sys_user_behavior_params
WHERE

       AND behavior_id = #{behaviorId, jdbcType=BIGINT}

DELETE FROM t_sys_user_behavior_params
WHERE


      AND behavior_id = #{behaviorId, jdbcType=BIGINT}


      AND request_id = #{requestId, jdbcType=VARCHAR}


      AND behavior_tags = #{behaviorTags, jdbcType=VARCHAR}


      AND behavior_time = #{behaviorTime, jdbcType=BIGINT}


      AND query_params = #{queryParams, jdbcType=LONGVARCHAR}


      AND body_params = #{bodyParams, jdbcType=LONGVARCHAR}

mapper.select:
SELECT 

  FROM t_sys_user_behavior_params a


      AND a.behavior_id = #{behaviorId, jdbcType=BIGINT}


      AND a.request_id = #{requestId, jdbcType=VARCHAR}


      AND a.behavior_tags = #{behaviorTags, jdbcType=VARCHAR}


      AND a.behavior_time = #{behaviorTime, jdbcType=BIGINT}


      AND a.query_params = #{queryParams, jdbcType=LONGVARCHAR}


      AND a.body_params = #{bodyParams, jdbcType=LONGVARCHAR}

SELECT count(1) FROM t_sys_user_behavior_params a


      AND a.behavior_id = #{behaviorId, jdbcType=BIGINT}


      AND a.request_id = #{requestId, jdbcType=VARCHAR}


      AND a.behavior_tags = #{behaviorTags, jdbcType=VARCHAR}


      AND a.behavior_time = #{behaviorTime, jdbcType=BIGINT}


      AND a.query_params = #{queryParams, jdbcType=LONGVARCHAR}


      AND a.body_params = #{bodyParams, jdbcType=LONGVARCHAR}
