mapper.delete:
DELETE FROM t_sys_user_behavior_response
WHERE

       AND request_id = #{requestId, jdbcType=VARCHAR}

DELETE FROM t_sys_user_behavior_response
WHERE


      AND request_id = #{requestId, jdbcType=VARCHAR}


      AND http_status = #{httpStatus, jdbcType=VARCHAR}


      AND response_code = #{responseCode, jdbcType=VARCHAR}


      AND response_time = #{responseTime, jdbcType=BIGINT}


      AND behavior_response = #{behaviorResponse, jdbcType=LONGVARCHAR}

mapper.select:
SELECT 

  FROM t_sys_user_behavior_response a


      AND a.request_id = #{requestId, jdbcType=VARCHAR}


      AND a.http_status = #{httpStatus, jdbcType=VARCHAR}


      AND a.response_code = #{responseCode, jdbcType=VARCHAR}


      AND a.response_time = #{responseTime, jdbcType=BIGINT}


      AND a.behavior_response = #{behaviorResponse, jdbcType=LONGVARCHAR}

SELECT count(1) FROM t_sys_user_behavior_response a


      AND a.request_id = #{requestId, jdbcType=VARCHAR}


      AND a.http_status = #{httpStatus, jdbcType=VARCHAR}


      AND a.response_code = #{responseCode, jdbcType=VARCHAR}


      AND a.response_time = #{responseTime, jdbcType=BIGINT}


      AND a.behavior_response = #{behaviorResponse, jdbcType=LONGVARCHAR}
